import { withStyles } from '@material-ui/core/styles';

import CustomBodyOfMonth from './CustomBodyOfMonth';
import { SCREEN_SIZE } from "../../constants";


const styles = (theme) => ({
  main: {
    fontSize: '1.2rem',
    padding: '0.35rem',
    border: 'solid #ddd 0.001rem',
  },

  number: {
    color: '#aeaeae',
    paddingRight: '0.5rem',
    fontSize: '1.5rem',
  },

  day: {
    // padding: '0 0.5rem',
  },

  busy: {
    backgroundColor: 'rgba(77,184,74,0.43)',
  },

  emptyContainer: {},

  holiday: {
    backgroundColor: '#5bc0de',
    color: 'white',
  },


  infoEmpty: {
    backgroundColor: '#ff747e',
  },

  info: {
    fontSize: '0.9rem',
    borderRadius: '0.5rem',
    padding: '0.1rem 0.4rem',
    float: 'right',

    [theme.breakpoints.up('sm')]: {
      marginRight: '1rem',
    },

  },

  infoBusy: {
    backgroundColor: '#55c7bd',
  },

  infoHoliday: {
    backgroundColor: 'white',
    color: 'black'
  },

  details: {
    fontSize: '0.7rem',
    margin: '0.2rem',
  },

  rowContainer: {
    display: 'grid',
    gridTemplateColumns: '5fr 1fr',
  },
  detailsMainContainer: {
    display: 'flex',
    flexDirection: 'column',
  },
  statusContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  rowInfo: {
    fontSize: '0.7rem',
  },
  icon: {
    width: '2rem',
  }
});

export default withStyles(styles)(CustomBodyOfMonth);
